CREATE FUNCTION report_location("from" text, "to" text) RETURNS TABLE(id integer, name character varying, state_code character varying, total_booking integer, total_tour_guide integer)
    LANGUAGE plpgsql
    AS $$
    BEGIN
    
      RETURN QUERY
      SELECT
        cities.id,
        cities.name,
        cities.state_code,
        booking_statistics.total_booking :: INTEGER,
        guide_statistics.total_tour_guide :: INTEGER
      FROM cities
        INNER JOIN LATERAL (
                   SELECT COUNT(*) AS total_booking
                   FROM bookings
                   WHERE bookings.city_id = cities.id
                         AND (bookings.created_at::DATE >= "from" :: DATE OR "from" IS NULL)
                         AND (bookings.created_at::DATE <= "to" :: DATE OR "to" IS NULL)
                   ) AS booking_statistics ON TRUE
        INNER JOIN LATERAL (
                   SELECT COUNT(*) AS total_tour_guide
                   FROM tour_guides
                     INNER JOIN guide_cities
                       ON guide_cities.city_id = cities.id AND tour_guides.id = guide_cities.tour_guide_id
                   WHERE (tour_guides.created_at::DATE >= "from" :: DATE OR "from" IS NULL)
                         AND (tour_guides.created_at::DATE <= "to" :: DATE OR "to" IS NULL)
                   ) AS guide_statistics ON TRUE;
    
    END
    $$;


