CREATE FUNCTION report_tour_guide(_from text, _to text, _city_id integer, _gender text, _account text, _first_name text, _last_name text, _email text, _background_checked boolean) RETURNS TABLE(id integer, register_date timestamp without time zone, first_name character varying, last_name character varying, gender enum_tour_guides_gender, email character varying, dob date, phone character varying, city_name text, total_booking integer, total_booking_finsihed integer, account character varying, status character varying, background_checked boolean)
    LANGUAGE plpgsql
    AS $$
    BEGIN
    
      RETURN QUERY
      SELECT
        tour_guides.id :: INTEGER                                                       AS id,
        tour_guides.created_at :: TIMESTAMP(0)                                          AS register_date,
        tour_guides.first_name                                                          AS first_name,
        tour_guides.last_name                                                           AS last_name,
        tour_guides.gender                                                              AS gender,
        tour_guides.email                                                               AS email,
