        (CASE WHEN tour_guides.background_checked_at IS NULL THEN FALSE ELSE TRUE END)  AS background_checked_at
      FROM tour_guides
        LEFT JOIN LATERAL (
                  SELECT
                    array_agg(cities.id)     AS city_ids,
