            AND (tour_guides.gender = _gender :: enum_tour_guides_gender OR _gender IS NULL)
            AND (tour_guides.guide_type = _account OR _account IS NULL)
            AND (tour_guides.first_name ILIKE '%' || _first_name || '%' OR _first_name IS NULL)
            AND (tour_guides.last_name ILIKE '%' || _last_name || '%' OR _last_name IS NULL)
            AND (tour_guides.email ILIKE '%' || _email || '%' OR _email IS NULL)
            AND (
                (tour_guides.background_checked_at IS NULL AND _background_checked = FALSE)
                OR
                (tour_guides.background_checked_at IS NOT NULL AND _background_checked = TRUE)
                OR
                _background_checked IS NULL
            );
    END
    $$;


