CREATE FUNCTION report_training_center(_id text, _from text, _to text, _city_id integer, _gender text, _account text, _lead_tour boolean, _graduated boolean, _first_name text, _last_name text, _email text) RETURNS TABLE(id integer, register_date timestamp without time zone, first_name character varying, last_name character varying, gender enum_tour_guides_gender, email character varying, dob date, phone character varying, city_name text, account character varying, status character varying, total_course integer, total_course_passed integer, graduated boolean, lead_tour boolean)
    LANGUAGE plpgsql
    AS $$
    BEGIN
    
      RETURN QUERY
    
      SELECT
        tour_guides.id :: INTEGER                               AS id,
        tour_guides.created_at :: TIMESTAMP(0)                  AS register_date,
        tour_guides.first_name                                  AS first_name,
        tour_guides.last_name                                   AS last_name,
        tour_guides.gender                                      AS gender,
        tour_guides.email                                       AS email,
        tour_guides.date_of_birth :: DATE                       AS dob,
        tour_guides.phone                                       AS phone,
        c.city_name                                             AS city_name,
        tour_guides.guide_type                                  AS account,
        tour_guides.status                                      AS status,
        course_statistic.total_course :: INTEGER                AS total_course,
        course_statistic.total_course_passed :: INTEGER         AS total_course_passed,
        course_statistic.graduated :: BOOLEAN                   AS graduated,
        course_statistic.lead_tour :: BOOLEAN                   AS lead_tour
      FROM tour_guides
        LEFT JOIN LATERAL (
                  SELECT
                    array_agg(cities.id)     AS city_ids,
                    string_agg("name", '; ') AS city_name
                  FROM cities
                    INNER JOIN guide_cities
                      ON guide_cities.city_id = cities.id AND guide_cities.tour_guide_id = tour_guides.id
    
                  ) AS c ON TRUE
    
        LEFT JOIN LATERAL (
                  SELECT
                    SUM(test_statistic.total_course)         AS total_course,
                    SUM(test_statistic.total_course_passed)  AS total_course_passed,
                    bool_or(test_statistic.graduated = TRUE) AS lead_tour,
                    every(test_statistic.graduated = TRUE)   AS graduated
                  FROM (
                         SELECT
                           tc_test_tour_guides.course_id,
                           tc_test_tour_guides.locale,
                           coalesce(SUM((tc_test_tour_guides.status = 'passed') :: INT), 0) AS total_course_passed,
                           COUNT(*)                                     AS total_course,
                           (CASE WHEN coalesce(SUM((tc_test_tour_guides.status = 'passed') :: INT), 0) = COUNT(*)
                             THEN TRUE
                            ELSE FALSE END)                             AS graduated
                         FROM tc_test_tour_guides
                           INNER JOIN LATERAL (
                                      SELECT array_agg(tc_course_city_relations.city_id) AS city_ids
                                      FROM tc_course_city_relations
                                      WHERE tc_course_city_relations.course_id = tc_test_tour_guides.course_id
                                      ) AS tc_cities ON TRUE
                         WHERE type = 'normal'
                               AND (_city_id = ANY (tc_cities.city_ids) OR _city_id IS NULL)
                               AND tc_test_tour_guides.email = tour_guides.email
                         GROUP BY tc_test_tour_guides.course_id, tc_test_tour_guides.locale
                       ) AS test_statistic
                  ) AS course_statistic ON TRUE
      WHERE (tour_guides.created_at >= _from :: TIMESTAMP(0) OR _from IS NULL)
            AND (tour_guides.created_at <= _to :: TIMESTAMP(0) OR _to IS NULL)
            AND (_city_id = ANY (c.city_ids) OR _city_id IS NULL)
            AND (tour_guides.gender = _gender :: enum_tour_guides_gender OR _gender IS NULL)
            AND (tour_guides.guide_type = _account OR _account IS NULL)
            AND (tour_guides.first_name ILIKE '%' || _first_name || '%' OR _first_name IS NULL)
            AND (tour_guides.last_name ILIKE '%' || _last_name || '%' OR _last_name IS NULL)
            AND (tour_guides.email ILIKE '%' || _email || '%' OR _email IS NULL)
            AND (course_statistic.graduated = _graduated OR _graduated IS NULL)
            AND (course_statistic.lead_tour = _lead_tour OR _lead_tour IS NULL)
            AND (tour_guides.id::TEXT ILIKE '%' || _id || '%' OR _id IS NULL);
    
    END
    $$;


