--
-- Name: ready_to_go; Type: VIEW; Schema: public; Owner: canarydbu
--

CREATE VIEW ready_to_go AS
 SELECT DISTINCT b.id,
    b.token,
    b.date,
    b."time",
    b.guests,
    b.remark,
    b.status,
    b.price,
    b.travellers,
    b.disables,
    b.created_at,
    b.traveller_id,
    b.tour_guide_id,
    b.city_id,
    b.code,
    b.begin_at,
    b.end_at,
    b.currency,
    b.time_zone,
    b.country_id,
    tg.phone AS tour_guide_phone,
    tv.phone AS traveller_phone,
    tg.email AS tour_guide_email,
    tv.email AS traveller_email,
    c.name AS city_name,
    (concat((b.date)::text, ' ', (b."time" ->> 'begin'::text), ':00:00'))::timestamp(0) without time zone AS start_in,
    (b.remark ->> 'pickUp'::text) AS pickup,
    to_char((b.date)::timestamp with time zone, 'Day DD/MM/YYYY'::text) AS pickup_date,
    concat(tg.first_name, ' ', tg.last_name) AS guide_name,
    tg.phone AS guide_phone,
    ((COALESCE(((b.travellers ->> 'adult'::text))::integer, 0) + COALESCE(((b.travellers ->> 'child'::text))::integer, 0)) + COALESCE(((b.travellers ->> 'elder'::text))::integer, 0)) AS total_traveller
   FROM ((((bookings b
     LEFT JOIN tour_guides tg ON ((tg.id = b.tour_guide_id)))
     LEFT JOIN travellers tv ON ((tv.id = b.traveller_id)))
     LEFT JOIN cities c ON ((c.id = b.city_id)))
     LEFT JOIN notifications n ON ((((n.type)::text = 'ready_to_go'::text) AND (((n.content ->> 'booking_id'::text))::integer = b.id))))
  WHERE (((((concat((b.date)::text, ' ', (b."time" ->> 'begin'::text), ':00:00'))::timestamp(0) without time zone - (now())::timestamp(0) without time zone) >= '00:00:00'::interval) AND (((concat((b.date)::text, ' ', (b."time" ->> 'begin'::text), ':00:00'))::timestamp(0) without time zone - (now())::timestamp(0) without time zone) <= '1 day'::interval)) AND ((b.status)::text = 'ready-to-go'::text) AND (n.id IS NULL));


