--
-- Name: report_booking; Type: VIEW; Schema: public; Owner: canarydbu
--

CREATE VIEW report_booking AS
 SELECT bookings.token,
    (bookings.created_at)::timestamp(0) without time zone AS booking_time,
    bookings.id AS booking_id,
    bookings.code AS booking_code,
    (bookings.code)::text AS text_booking_code,
    to_char((concat(bookings.date, ' ', (bookings."time" ->> 'begin'::text), ':00:00'))::timestamp(0) without time zone, 'DD Mon YYYY HH24:MI'::text) AS departure_time,
    (concat(bookings.date, ' ', (bookings."time" ->> 'begin'::text), ':00:00'))::timestamp(0) without time zone AS ts_departure_time,
    (((bookings."time" ->> 'end'::text))::integer - ((bookings."time" ->> 'begin'::text))::integer) AS duration,
    concat(cities.name, '.', cities.state_code) AS city,
    concat(tour_guides.first_name, ' ', tour_guides.last_name) AS tour_guide,
    concat(travellers.first_name, ' ', travellers.last_name) AS traveller,
    (bookings.price ->> 'origin'::text) AS origin_price,
    (bookings.price ->> 'traveller_price'::text) AS traveller_price,
    (bookings.price ->> 'tour_guide_price'::text) AS tour_guide_price,
    refunds.penalty_charge,
        CASE
            WHEN (refunds.id IS NULL) THEN transactions.status
            ELSE 'refund'::character varying
        END AS payment,
    bookings.status,
    comments.star_point AS rating,
    transactions.id AS transaction_id,
    tour_guides.id AS tour_guide_id,
    cities.id AS city_id
   FROM ((((((bookings
     JOIN cities ON ((cities.id = bookings.city_id)))
     LEFT JOIN tour_guides ON ((tour_guides.id = bookings.tour_guide_id)))
     LEFT JOIN travellers ON ((travellers.id = bookings.traveller_id)))
     LEFT JOIN refunds ON ((refunds.booking_id = bookings.id)))
     LEFT JOIN transactions ON (((bookings.id)::numeric = ANY (transactions.booking_ids))))
     LEFT JOIN comments ON (((comments.booking_id = bookings.id) AND ((comments.type)::text = 'review'::text))));


