--
-- Name: waiting_to_review; Type: VIEW; Schema: public; Owner: canarydbu
--

CREATE VIEW waiting_to_review AS
 SELECT DISTINCT b.id,
    b.token,
    b.date,
    b."time",
    b.guests,
    b.remark,
    b.status,
    b.price,
    b.travellers,
    b.disables,
    b.created_at,
    b.traveller_id,
    b.tour_guide_id,
    b.city_id,
    b.code,
    b.begin_at,
    b.end_at,
    b.currency,
    b.time_zone,
    b.country_id,
    tg.phone AS tour_guide_phone,
    tv.phone AS traveller_phone,
    tg.email AS tour_guide_email,
    tv.email AS traveller_email,
    c.name AS city_name,
    (concat((b.date)::text, ' ', (b."time" ->> 'begin'::text), ':00:00'))::timestamp(0) without time zone AS start_in
   FROM (((((bookings b
     LEFT JOIN tour_guides tg ON ((tg.id = b.tour_guide_id)))
     LEFT JOIN travellers tv ON ((tv.id = b.traveller_id)))
     LEFT JOIN cities c ON ((c.id = b.city_id)))
     LEFT JOIN comments comment ON (((comment.booking_id = b.id) AND ((comment.type)::text = 'review'::text))))
     LEFT JOIN notifications n ON (((((n.content ->> 'booking_id'::text))::integer = b.id) AND ((n.type)::text = 'waiting_to_review'::text))))
  WHERE (((((concat((b.date)::text, ' ', (b."time" ->> 'end'::text), ':00:00'))::timestamp(0) without time zone - (now())::timestamp(0) without time zone) >= '-1 days'::interval) AND (((concat((b.date)::text, ' ', (b."time" ->> 'end'::text), ':00:00'))::timestamp(0) without time zone - (now())::timestamp(0) without time zone) <= '00:00:00'::interval)) AND ((b.status)::text = 'ready-to-go'::text) AND (comment.id IS NULL) AND (n.id IS NULL));


